import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Card, CardContent, Typography, Button, Avatar } from '@mui/material';
import axios from 'axios';

function CandidateDetail() {
    const { id } = useParams();
    const [candidat, setCandidat] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:5000/api/candidats/${id}`)
            .then(response => setCandidat(response.data))
            .catch(error => console.error("Erreur :", error));
    }, [id]);

    if (!candidat) return <Typography>Chargement...</Typography>;

    return (
        <Card sx={{ maxWidth: 500, margin: 'auto', padding: 3 }}>
            <Avatar src={candidat.photo || "/default-avatar.png"} sx={{ width: 100, height: 100, margin: 'auto' }} />
            <CardContent>
                <Typography variant="h5">{candidat.nom} {candidat.prenom}</Typography>
                <Typography variant="body1">Email: {candidat.email}</Typography>
                <Typography variant="body1">Téléphone: {candidat.telephone}</Typography>
                <Typography variant="body1">Parti: {candidat.parti || "Indépendant"}</Typography>
                <Button variant="contained" color="secondary">Générer un nouveau code</Button>
            </CardContent>
        </Card>
    );
}

export default CandidateDetail;
