import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import CandidateRegistration from './components/CandidateRegistration';
import CandidateList from './components/CandidateList';
import CandidateDetail from './components/CandidateDetail';
import ParrainageConfig from './components/ParrainageConfig';



function App() {
  return (
      <Router>
        <nav style={{ padding: 10, borderBottom: "1px solid #ccc" }}>
          <Link to="/" style={{ marginRight: 10 }}>Accueil</Link>
          <Link to="/candidats" style={{ marginRight: 10 }}>Liste des Candidats</Link>
          <Link to="/candidats/new" style={{ marginRight: 10 }}>Ajouter Candidat</Link>
          <Link to="/parrainage" style={{ marginRight: 10 }}>Configurer Parrainage</Link>
        </nav>

        <Routes>
          <Route path="/" element={<h1>Bienvenue dans le syst√®me de gestion des parrainages</h1>} />
          <Route path="/candidats" element={<CandidateList />} />
          <Route path="/candidats/new" element={<CandidateRegistration />} />
          <Route path="/candidats/:id" element={<CandidateDetail />} />
          <Route path="/parrainage" element={<ParrainageConfig />} />
        </Routes>
      </Router>
  );
}

export default App;
