import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function CandidateList() {
    const [candidats, setCandidats] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchCandidats = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/candidats');
                setCandidats(response.data);
            } catch (err) {
                setError("Erreur lors de la récupération des candidats.");
            }
        };
        fetchCandidats();
    }, []);

    return (
        <div className="max-w-5xl mx-auto mt-6">
            <h2 className="text-2xl font-semibold text-center mb-6">Liste des Candidats</h2>
            {error && <p className="text-red-500 text-center">{error}</p>}
            {candidats.length === 0 ? (
                <p className="text-center text-gray-500">Aucun candidat enregistré.</p>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    {candidats.map((c) => (
                        <Link to={`/candidats/${c.id}`} key={c.id} className="block p-4 bg-white rounded-lg shadow-md hover:shadow-lg transition-all">
                            <div className="flex flex-col items-center">
                                <img src={c.photo || "/default-avatar.png"} alt={c.name} className="w-24 h-24 rounded-full mb-3" />
                                <h3 className="text-lg font-semibold">{c.nom} {c.prenom}</h3>
                                <p className="text-gray-500">{c.parti || "Indépendant"}</p>
                            </div>
                        </Link>
                    ))}
                </div>
            )}
        </div>
    );
}

export default CandidateList;
