import React, { useState } from 'react';

function ParrainageConfig() {
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        setError('');
        setMessage('');

        const start = new Date(startDate);
        const end = new Date(endDate);
        const today = new Date();

        // Vérifications des dates
        if (start >= end) {
            setError("La date de début doit être avant la date de fin.");
        } else if (start <= today) {
            setError("La date de début doit être dans le futur (au moins 6 mois après aujourd'hui).");
        } else {
            setMessage(`Période de parrainage configurée du ${startDate} au ${endDate}.`);
            // Tu peux ici envoyer les dates au backend via une API
        }
    };

    return (
        <div className="max-w-xl mx-auto p-6 bg-white rounded-lg shadow-md mt-6">
            <h2 className="text-2xl font-semibold mb-4 text-center">Configurer la Période de Parrainage</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label htmlFor="startDate" className="block text-lg">Date de début :</label>
                    <input
                        type="date"
                        id="startDate"
                        value={startDate}
                        onChange={(e) => setStartDate(e.target.value)}
                        className="w-full p-2 border border-gray-300 rounded-lg"
                    />
                </div>

                <div>
                    <label htmlFor="endDate" className="block text-lg">Date de fin :</label>
                    <input
                        type="date"
                        id="endDate"
                        value={endDate}
                        onChange={(e) => setEndDate(e.target.value)}
                        className="w-full p-2 border border-gray-300 rounded-lg"
                    />
                </div>

                <div className="flex justify-between items-center">
                    <button type="submit" className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-700">Enregistrer</button>
                </div>

                {error && <p className="text-red-500 mt-2">{error}</p>}
                {message && <p className="text-green-500 mt-2">{message}</p>}
            </form>
        </div>
    );
}

export default ParrainageConfig;
