// src/components/Parrainage.js
import React, { useState } from 'react';
import { Container, Paper, Typography, TextField, Button, Grid } from '@mui/material';
import axios from 'axios';

function Parrainage() {
    const [numCarteElecteur, setNumCarteElecteur] = useState('');
    const [candidates, setCandidates] = useState([]);
    const [selectedCandidate, setSelectedCandidate] = useState(null);
    const [message, setMessage] = useState('');

    // Rechercher des candidats à partir du numéro de carte ou d'un critère
    const handleSearchCandidate = async () => {
        setMessage('');
        try {
            // Ici, nous appelons une API de recherche (par exemple GET /api/candidats/search?query=...)
            // Pour cet exemple, on suppose que l'API renvoie une liste de candidats correspondant à la recherche.
            const response = await axios.get(`http://localhost:5000/api/candidats/search?query=${numCarteElecteur}`);
            setCandidates(response.data);
            if (response.data.length === 0) {
                setMessage("Aucun candidat trouvé pour ce numéro.");
            }
        } catch (error) {
            console.error(error);
            setMessage("Erreur lors de la recherche des candidats.");
        }
    };

    // Lorsque l'utilisateur clique sur un candidat, on le sélectionne
    const handleSelectCandidate = (candidate) => {
        setSelectedCandidate(candidate);
    };

    // Envoyer le parrainage
    const handleParrainage = async () => {
        setMessage('');
        try {
            // Appel à l'API backend pour enregistrer le parrainage
            // Par exemple, POST /api/parrainage avec { numCarteElecteur, candidateId }
            const response = await axios.post('http://localhost:5000/api/parrainage', {
                numCarteElecteur,
                candidateId: selectedCandidate.id,
            });
            setMessage("Parrainage enregistré avec succès !");
            // Réinitialiser les états si nécessaire
            setSelectedCandidate(null);
            setCandidates([]);
            setNumCarteElecteur('');
        } catch (error) {
            console.error(error);
            setMessage("Erreur lors de l'enregistrement du parrainage.");
        }
    };

    return (
        <Container maxWidth="sm">
            <Paper elevation={3} sx={{ p: 4, mt: 4 }}>
                <Typography variant="h5" textAlign="center" gutterBottom>
                    Enregistrer un Parrainage
                </Typography>
                <TextField
                    fullWidth
                    label="Numéro de carte électeur"
                    value={numCarteElecteur}
                    onChange={(e) => setNumCarteElecteur(e.target.value)}
                    sx={{ mb: 2 }}
                />
                <Button variant="contained" color="primary" fullWidth onClick={handleSearchCandidate}>
                    Rechercher Candidats
                </Button>
                {candidates.length > 0 && (
                    <Grid container spacing={2} sx={{ mt: 2 }}>
                        {candidates.map((candidate) => (
                            <Grid item xs={12} key={candidate.id}>
                                <Paper
                                    sx={{
                                        p: 2,
                                        cursor: 'pointer',
                                        border: selectedCandidate && selectedCandidate.id === candidate.id ? '2px solid blue' : 'none',
                                    }}
                                    onClick={() => handleSelectCandidate(candidate)}
                                >
                                    <Typography variant="h6">
                                        {candidate.nom} {candidate.prenom}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                        Parti: {candidate.parti || 'Indépendant'}
                                    </Typography>
                                </Paper>
                            </Grid>
                        ))}
                    </Grid>
                )}
                {selectedCandidate && (
                    <>
                        <Typography variant="h6" textAlign="center" sx={{ mt: 2 }}>
                            Candidat sélectionné: {selectedCandidate.nom} {selectedCandidate.prenom}
                        </Typography>
                        <Button variant="contained" color="secondary" fullWidth sx={{ mt: 2 }} onClick={handleParrainage}>
                            Confirmer le Parrainage
                        </Button>
                    </>
                )}
                {message && (
                    <Typography textAlign="center" sx={{ mt: 2 }} color="primary">
                        {message}
                    </Typography>
                )}
            </Paper>
        </Container>
    );
}

export default Parrainage;
