import React, { useState } from 'react';
import { Container, Paper, Typography, TextField, Button } from '@mui/material';

function ParrainageConfig() {
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const start = new Date(startDate);
        const end = new Date(endDate);
        const today = new Date();

        if (start >= end) {
            setMessage("❌ La date de début doit être avant la date de fin.");
        } else if (start <= today) {
            setMessage("❌ La date de début doit être dans le futur.");
        } else {
            setMessage(`✅ Période de parrainage configurée du ${startDate} au ${endDate}.`);
        }
    };

    return (
        <Container maxWidth="sm">
            <Paper elevation={3} sx={{ padding: 4, marginTop: 4 }}>
                <Typography variant="h5" textAlign="center" gutterBottom>
                    Configuration du Parrainage
                </Typography>
                <form onSubmit={handleSubmit}>
                    <TextField fullWidth type="date" label="Date de début" value={startDate} onChange={(e) => setStartDate(e.target.value)} sx={{ marginBottom: 2 }} />
                    <TextField fullWidth type="date" label="Date de fin" value={endDate} onChange={(e) => setEndDate(e.target.value)} sx={{ marginBottom: 2 }} />
                    <Button type="submit" variant="contained" color="primary" fullWidth>
                        Enregistrer la Période
                    </Button>
                    {message && <Typography textAlign="center" sx={{ marginTop: 2 }}>{message}</Typography>}
                </form>
            </Paper>
        </Container>
    );
}

export default ParrainageConfig;
