import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Card, CardContent, Typography, Grid, Button, Avatar } from '@mui/material';
import axios from 'axios';

function CandidateList() {
    const [candidats, setCandidats] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5000/api/candidats')
            .then(response => setCandidats(response.data))
            .catch(error => console.error("Erreur de récupération :", error));
    }, []);

    return (
        <Grid container spacing={3}>
            {candidats.map((candidat) => (
                <Grid item xs={12} sm={6} md={4} key={candidat.id}>
                    <Card sx={{ textAlign: 'center', padding: 2 }}>
                        <Avatar src={candidat.photo || "/default-avatar.png"} sx={{ width: 80, height: 80, margin: 'auto' }} />
                        <CardContent>
                            <Typography variant="h6">{candidat.nom} {candidat.prenom}</Typography>
                            <Typography color="text.secondary">{candidat.parti || "Indépendant"}</Typography>
                            <Button variant="contained" color="primary" component={Link} to={`/candidats/${candidat.id}`}>
                                Voir détails
                            </Button>
                        </CardContent>
                    </Card>
                </Grid>
            ))}
        </Grid>
    );
}

export default CandidateList;
