import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function CandidateDetail() {
    const { id } = useParams();
    const [candidat, setCandidat] = useState(null);
    const [message, setMessage] = useState('');

    useEffect(() => {
        const fetchCandidat = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/candidats/${id}`);
                setCandidat(response.data);
            } catch (err) {
                setMessage("Erreur lors de la récupération du candidat.");
            }
        };
        fetchCandidat();
    }, [id]);

    const handleRegenerateCode = async () => {
        try {
            setMessage('');
            const response = await axios.patch(`http://localhost:5000/api/candidats/${id}/code`);
            setMessage(`Nouveau code généré : ${response.data.code_securite}`);
        } catch (err) {
            setMessage("Erreur lors de la génération du code.");
        }
    };

    if (!candidat) return <div className="text-center mt-6">Chargement...</div>;

    return (
        <div className="max-w-3xl mx-auto bg-white p-6 rounded-lg shadow-md mt-6">
            <h2 className="text-2xl font-semibold text-center mb-4">Détails du Candidat</h2>
            <div className="flex items-center justify-center">
                <img src={candidat.photo || "/default-avatar.png"} alt="Candidat" className="w-32 h-32 rounded-full mb-4" />
            </div>
            <p className="text-lg"><strong>Nom :</strong> {candidat.nom} {candidat.prenom}</p>
            <p className="text-lg"><strong>Date de Naissance :</strong> {candidat.date_naissance}</p>
            <p className="text-lg"><strong>Email :</strong> {candidat.email}</p>
            <p className="text-lg"><strong>Téléphone :</strong> {candidat.telephone}</p>
            <p className="text-lg"><strong>Parti :</strong> {candidat.parti || "Indépendant"}</p>
            <p className="text-lg"><strong>Slogan :</strong> {candidat.slogan}</p>
            <p className="text-lg"><strong>Couleurs :</strong> {candidat.couleur1}, {candidat.couleur2}, {candidat.couleur3}</p>
            <p className="text-lg"><strong>Page info :</strong> <a href={candidat.url_info} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:underline">Lien</a></p>
            <p className="text-lg"><strong>Code Sécurité :</strong> {candidat.code_securite}</p>

            <button onClick={handleRegenerateCode} className="bg-red-500 text-white px-4 py-2 mt-4 rounded hover:bg-red-700">
                Générer nouveau code
            </button>
            {message && <p className="text-green-500 mt-2">{message}</p>}
        </div>
    );
}

export default CandidateDetail;
